mapper.select:
SELECT course, count(*) as number FROM emr_ucc where date>=#{startTime}
and date<=#{endTime} and street in

    #{city}

GROUP BY course

SELECT isMarried, count(*) as number FROM emr_ucc where date>=#{startTime}
and date<=#{endTime} and street in

    #{city}

GROUP BY isMarried

SELECT profession, count(*) as number FROM emr_ucc where date>=#{startTime}
and date<=#{endTime} and  street in

    #{city}

 GROUP BY profession

SELECT  COUNT(*) number
FROM emr_ucc WHERE age>=0 and age <=4 AND gender='男' and date>=#{startTime}
and date<=#{endTime}  and street in

    #{city}

UNION ALL
